import { Box, Button, Card, CardContent, Container, Dialog, TextField, Typography } from "@mui/material";
import axios, { AxiosError } from "axios";
import { useState } from "react";
import globals from "../../services/Globals";
import ResultDialog from "../ResultDialog";
import LoadingDialog from "../LoadingDialog";




function Main(): JSX.Element {
    const [open, setOpen] = useState(false);

    const handleClose = () => {
      setOpen(false);
    };  

    const [phone, setPhone] = useState<string>("");
    const [result, setResult] = useState<boolean>(false);
    const [loading, setLoading] = useState<boolean>(false);

    const checkPhoneOnDNC = async (phone: string) => {
      return axios.get<boolean>(globals.urls.phoneQuery + phone);
    }


    const phoneQuery = async () => {
        if(phone.length >= 9 && phone.length <= 10 && phone.startsWith("0")) {
          setLoading(true);
          checkPhoneOnDNC(phone)
         .then((res) => {
            setResult(res.data);
            setLoading(false);
            setOpen(true);
         })
         .catch((reason: AxiosError) => {
            setLoading(false);
        })
        } else {
            alert("מספר הטלפון שהזנת אינו תקין. המספר צריך להיות בין 9-10 ספרות בלבד ולהתחיל בספרה 0");
        }
    }


    return (
        <div className="Main">  

<LoadingDialog isLoading={loading} />

       <Dialog open={open}>
      <ResultDialog isOpen={open} result={result}/>
        <Button
        onClick={handleClose}> אישור </Button>
      </Dialog>

<Typography>שירות זה מאפשר לבדוק האם מספר טלפון רשום למאגר ״אל תתקשר אליי״</Typography>
<Typography>הוא נוצר בעקבות תיקון חקיקה לחוק הגנת הצרכן (תיקון מס' 61) הקובע כי הרשות תקים ותנהל מאגר שבו יירשמו מספרי טלפון של צרכנים המעוניינים להגביל פנייה שיווקית אליהם מאת עוסקים או מי מטעמם. </Typography>

        <Card>
                <Typography align='center' fontWeight="bold" mt={5}>
                   הזן מספר טלפון לבדיקה
                </Typography>

            <CardContent>
        <TextField
         fullWidth 
         variant="filled" 
         onChange={e => (setPhone(e.target.value))
        }
         />
<Container style={{padding:"20px", display:"flex", flexDirection:"column", alignItems:"center"}}>
        <Button onClick={phoneQuery}>בדיקה במאגר</Button>
        </Container>
        </CardContent>
        </Card>



        </div>
    );


}

export default Main;
