import DoDisturbIcon from '@mui/icons-material/DoDisturb';import { Button, Card, CardContent, Dialog, DialogContent, DialogTitle, Icon, IconButton, TextField, Typography } from "@mui/material";
import CheckIcon from '@mui/icons-material/Check';
import axios, { AxiosError } from "axios";
import { useState } from "react";
import globals from "../../Services/Globals";
import "./Main.css";





  export default function ResultDialog() {
    const [open, setOpen] = useState(false);

    const handleClose = () => {
      setOpen(false);
    };  

    const [phone, setPhone] = useState<string>("");
    const [result, setResult] = useState<boolean>();
    const okMessage = "מספר זה אינו רשום למאגר"
    const noMessage = "מספר זה רשום למאגר";
    const phoneQuery = async () => {

        if(phone.length >= 9 && phone.length <= 10 && phone.startsWith("0")) {
        axios.get<boolean>(globals.urls.phoneQuery + phone)

         .then((res) => {
            setResult(res.data)
            setOpen(true);
         })
         .catch((reason: AxiosError) => {
            if (reason.response!.status === 400) {
              alert("מספר הטלפון שהזנת אינו תקין. המספר צריך להיות בין 9-10 ספרות בלבד ולהתחיל בספרה 0");
            }  
        })
        } else {
            alert("מספר הטלפון שהזנת אינו תקין. המספר צריך להיות בין 9-10 ספרות בלבד ולהתחיל בספרה 0");
        }


    }
    return (
        <div className="Main">  

       <Dialog open={open}>

        <DialogContent style={{ padding: '50px, 50px, 50px, 50px' }}>
        <DialogTitle align='center'>הבדיקה הושלמה</DialogTitle>
        <Typography align='center'>      
        {(result == true) ? 

        <CheckIcon color="success" sx={{ fontSize: "80px" }}/>
        : 
        <DoDisturbIcon color="error" sx={{ fontSize: "80px" }}/> }
        </Typography>
            <Typography fontWeight='bold' align='center'>{
            (result == true) ? okMessage : noMessage
            }</Typography>
             <Typography align='center'>{
            (result == true) ? 'ניתן לבצע שיחת טלמרקטינג' : 'לא ניתן לבצע שיחת טלמרקטינג'
            }</Typography>
        </DialogContent>
        <Button
                 style={{
                    color: "#8f7d56",
                }}
        onClick={handleClose}> אישור </Button>
      </Dialog>


        <Card>
                <Typography align='center' mt={5}>
                   הזן מספר טלפון לבדיקה
                </Typography>

            <CardContent>
        <TextField
         fullWidth 
         variant="filled" 
         onChange={e => (setPhone(e.target.value))
        }
         />
        <Button  
         style={{
            backgroundColor: "#8f7d56",
            color: "#fff",
            padding: "18px 36px",
            fontSize: "18px"
        }}
        sx={{ m: 3 }}  onClick={phoneQuery}> בדיקה במאגר</Button>
        </CardContent>
        </Card>
        </div>
    );
}
