import { Button, Card, CardContent, Dialog, TextField, Typography } from "@mui/material";
import axios, { AxiosError } from "axios";
import { useState } from "react";
import globals from "../../services/Globals";
import ResultDialog from "../ResultDialog";



function Main(): JSX.Element {
    const [open, setOpen] = useState(false);

    const handleClose = () => {
      setOpen(false);
    };  

    const [phone, setPhone] = useState<string>("");
    const [result, setResult] = useState<boolean>(false);
    const [loading, setLoading] = useState<boolean>(false);

    const checkPhoneOnDNC = async (phone: string) => {
      return axios.get<boolean>(globals.urls.phoneQuery + phone);
    }


    const phoneQuery = async () => {
        if(phone.length >= 9 && phone.length <= 10 && phone.startsWith("0")) {
          setLoading(true);
          checkPhoneOnDNC(phone)
         .then((res) => {
            setResult(res.data);
            setLoading(false);
            setOpen(true);
         })
         .catch((reason: AxiosError) => {
            setLoading(false);
        })
        } else {
            alert("מספר הטלפון שהזנת אינו תקין. המספר צריך להיות בין 9-10 ספרות בלבד ולהתחיל בספרה 0");
        }
    }


