        <div className="Main">  

<LoadingDialog isLoading={isLoading} />

       <Dialog open={open}>
      <ResultDialog isOpen={open} result={result}/>
        <Button
                 style={{
                    color: "#8f7d56",
                }}
        onClick={handleClose}> אישור </Button>
      </Dialog>


        <Card>
                <Typography align='center' mt={5}>
                   הזן מספר טלפון לבדיקה
                </Typography>

            <CardContent>
        <TextField
         fullWidth 
         variant="filled" 
         onChange={e => (setPhone(e.target.value))
        }
         />
        <Button variant="outlined" onClick={phoneQuery}> בדיקה במאגר</Button>
        </CardContent>
        </Card>
        </div>
    );


}

export default Main;
